$(document).ready(function(){
	$.ajax({
		url: "http://localhost/Library-Management-System/admin/node_modules/startbootstrap-sb-admin-2/chartjs/php-mysql-chartjs/data.php",
		method: "GET",
		success: function(data) {
			console.log(data);
			var libid = [];
			var elapsed_time = [];

			for(var i in data) {
				libid.push(data[i].lib_id);
				elapsed_time.push(data[i].elapsed_time);
			}

			var chartdata = {
				labels: elapsed_time,
				datasets : [
					{
						label: 'Lib ID',
						backgroundColor: 'rgba(200, 200, 200, 0.75)',
						borderColor: 'rgba(200, 200, 200, 0.75)',
						hoverBackgroundColor: 'rgba(200, 200, 200, 1)',
						hoverBorderColor: 'rgba(200, 200, 200, 1)',
						data: libid
					}
				],
				options: {
					maintainAspectRatio: false,
				animation: false,
					legend: {display: true  },
				scales:{xAxes: [{type: 'time',time: {unit: 'day'}}]}
					}
			};

			var ctx = $("#mycanvas");

			var barGraph = new Chart(ctx, {
				type: 'bar',
				data: chartdata
			});
		},
		error: function(data) {
			console.log(data);
		}
	});
});