$(document).ready(function(){
	$.ajax({
		url : "http://localhost/Library-Management-System/admin/node_modules/startbootstrap-sb-admin-2/chartjs/php-mysql-chartjs/followersdata.php",
		type : "GET",
		success : function(data){
			console.log(data);

			var libid = [];
			var elapsed_time = [];

			for(var i in data) {
				libid.push(data[i].lib_id);
				elapsed_time.push(data[i].elapsed_time);
			}
			console.log(libid);
			console.log(elapsed_time);

			var chartdata = {
				labels: libid,
				datasets: [
					{
						label: "elapsed_time",
						fill: false,
						lineTension: 0.1,
						backgroundColor: "rgba(59, 89, 152, 0.75)",
						borderColor: "rgba(59, 89, 152, 1)",
						pointHoverBackgroundColor: "rgba(59, 89, 152, 1)",
						pointHoverBorderColor: "rgba(59, 89, 152, 1)",
						data: elapsed_time
					}
				]
			};

			var ctx = $("#mycanvas");

			var LineGraph = new Chart(ctx, {
				type: 'line',
				data: chartdata
			});
		},
		error : function(data) {

		}
	});
});