<?php
    $con = mysqli_connect('127.0.0.1','root','mysql123');

    if(!$con)
    {
        echo 'Not connected to the server';
    }

    if(!mysqli_select_db($con, 'attendance_management_system'))
    {
        echo 'Database not selected';
    }
    $from=$_POST['from'];
    $to=$_POST['to'];
    $branch=$_POST['branch'];

//include database configuration file
// include 'dbConfig.php';

//get records from database
if($branch=="All")
$query = $con->query("SELECT name,department,Library_ledger.lib_id,status,entry,`exit`,elapsed_time,SUGGESTION FROM `Library_ledger`,`outsider_faculty` WHERE LIBRARY_LEDGER.LIB_ID=OUTSIDER_FACULTY.LIB_ID AND `entry` BETWEEN '$from.00:00:00' AND '$to.23:59:59' ORDER BY `entry` DESC");
else
$query = $con->query("SELECT name,department,Library_ledger.lib_id,status,entry,`exit`,elapsed_time,SUGGESTION FROM `Library_ledger`,`outsider_faculty` WHERE LIBRARY_LEDGER.LIB_ID=OUTSIDER_FACULTY.LIB_ID AND `entry` BETWEEN '$from.00:00:00' AND '$to.23:59:59' AND DEPARTMENT='$branch' ORDER BY `entry` DESC");

if($query->num_rows > 0){
    $delimiter = ",";
    $filename = "library_report_" . date('Y-m-d') . ".csv";
    
    //create a file pointer
    $f = fopen('php://memory', 'w');
    
    //set column headers
    $fields = array('Name','Department','Lib ID', 'Status','Entry Time', 'Exit Time', 'Elapsed Time', 'Feedback');
    fputcsv($f, $fields, $delimiter);
    
    //output each row of the data, format line as csv and write to file pointer
    while($row = $query->fetch_assoc()){
        $status = ($row['status'] == '1')?'In Library':' ';
        $lineData = array($row['name'], $row['department'], $row['lib_id'], $row['status'], $row['entry'], $row['exit'], $row['elapsed_time'], $row['SUGGESTION']);
        fputcsv($f, $lineData, $delimiter);
    }
    
    //move back to beginning of file
    fseek($f, 0);
    
    //set headers to download file rather than displayed
    header('Content-Type: text/csv');
    header('Content-Disposition: attachment; filename="' . $filename . '";');
    
    //output all remaining data on a file pointer
    fpassthru($f);
}
exit;

?>


